const widget_owner_id = "denbite.testnet";

let account_id = context.accountId;
let contract_id = props.contract_id;

console.log("account_id", account_id);
console.log("contract_id", contract_id);

// TODO: view spec to make sure it's appropriate contract
if (!contract_id) {
  return (
    <Widget
      src={`${widget_owner_id}/widget/MysteryBox.Messages.InvalidContract`}
    />
  );
}

State.init({
  now: Date.now(),
});

// interval time is doubled for some reason, so it's set to 500ms to update every second in fact
useCache(
  () =>
    setInterval(() => {
      State.update({
        now: Date.now(),
      });
    }, 950),
  "now_interval",
  { subscribe: false }
);

// TODO: remove styled containers for components to make them importable (only pages may contain containers)

if (!account_id) {
  return (
    <Widget
      src={`${widget_owner_id}/widget/MysteryBox.Messages.AuthenticationRequired`}
    />
  );
}

let registration_start_time = Near.view(
  contract_id,
  "get_registration_start_time"
);
let registration_end_time = Near.view(contract_id, "get_registration_end_time");
let claiming_start_time = Near.view(contract_id, "get_claiming_start_time");

console.log("now", state.now);
console.log("registration_start_time", registration_start_time);
console.log("registration_end_time", registration_end_time);
console.log("claiming_start_time", claiming_start_time);

// is still being set up
if (!registration_start_time) {
  return (
    <Widget
      src={`${widget_owner_id}/widget/MysteryBox.Messages.ContestWasNotSetUp`}
    />
  );
}

// countdown before start
if (state.now < registration_start_time) {
  return (
    <Widget
      src={`${widget_owner_id}/widget/TimeCountdown`}
      props={{
        time_left_ms: registration_start_time - state.now,
        text: "Registration is opening soon",
      }}
    />
  );
}

// registration open
if (!registration_end_time || state.now < registration_end_time) {
  return (
    <Widget
      src={`${widget_owner_id}/widget/MysteryBox.Components.Registration`}
      props={{
        contract_id: contract_id,
      }}
    />
  );
}

if (!claiming_start_time) {
  return (
    <Widget
      src={`${widget_owner_id}/widget/MysteryBox.Messages.RegistrationClosed`}
    />
  );
}

if (state.now < claiming_start_time) {
  return (
    <Widget
      src={`${widget_owner_id}/widget/TimeCountdown`}
      props={{
        time_left_ms: claiming_start_time - state.now,
        text: "Claiming is opening soon",
      }}
    />
  );
}

return (
  <Widget
    src={`${widget_owner_id}/widget/MysteryBox.Components.Claiming`}
    props={{
      contract_id: contract_id,
    }}
  />
);

